          <Route exact path = '/' component={withRouter(TextForm)}>
          <TextForm heading = "Enter the text to analyze Below : " showAlert={showAlert} mode={mode}/>

          </Route>
          <Route exact path="/about" component={withRouter(About)}>
            <About />


          </Route>
      </Switch>
      </div>

    </Router> */}



    </>
  )
}
export default App
